function dateToString(aDate) {
    switch (data.dateFormatString) {
        case "mdy":
            return (aDate.getMonth() + 1) + "/" + aDate.getDate() + "/" + (aDate.getYear() < 1900 ? aDate.getYear() + 1900 : aDate.getYear());
            break;
        case "dmy":
            return aDate.getDate() + "/" + (aDate.getMonth() + 1) + "/" + (aDate.getYear() < 1900 ? aDate.getYear() + 1900 : aDate.getYear());
            break;
        case "iso8601":
            return (aDate.getYear() < 1900 ? aDate.getYear() + 1900 : aDate.getYear()) + "/" + (aDate.getMonth() + 1) + "/" + aDate.getDate();
            break;
    }
    return "";
}
function parseDate(input, format) {
    format = format || "yyyy-mm-dd"; // default format
    var parts = input.match(/(\d+)/g);
    var i = 0;
    var fmt = {};
    // extract date-part indexes from the format
    format.replace(/(yyyy|dd|mm)/g, function (part) {
        fmt[part] = i++;
    });
    return new Date(parts[fmt["yyyy"]], parts[fmt["mm"]] - 1, parts[fmt["dd"]]);
}
function readDateFrom(aField) {
    if (!aField || aField.value == "") return "";
    switch (data.dateFormatString) {
        case "mdy":
            return parseDate(aField.value, "mm-dd-yyyy");
        case "dmy":
            return parseDate(aField.value, "dd-mm-yyyy");
        case "iso8601":
            return parseDate(aField.value, "yyyy-mm-dd");
    }
}
function resetMinDate(aField, aCalendar) {
    var checkIn = $(data.checkInIdString);
    var checkOut = $(data.checkOutIdString);
    if (aField.id == data.checkOutIdString && checkIn != null) {
        var minDate = readDateFrom(checkIn);
        calendarResetMinDate(aCalendar, minDate);
    }
    if (checkIn && checkOut) {
        var indate = readDateFrom(checkIn);
        var outdate = readDateFrom(checkOut);
        if (indate && (!outdate || indate >= outdate)) {
            outdate = new Date(indate);
            outdate.setDate(indate.getDate() + 1);
            checkOut.value = dateToString(outdate);
            fireEvent(checkOut, "change");
        }
    }
}
function main() {
    var textField = $(data.calIdString);
    var cal = calendarNew(data.nextIdString, data.containerIdString);
